TL.loading = function(action) {
  if (action == "start") {
    var over = '<div id="overlay">' +
            '<img id="loading" src="http://i201.photobucket.com/albums/aa236/Mottie1/testsite/icons/loading.gif">' +
            '</div>';
    $(over).appendTo('body');
  }else{
    $('#overlay').remove();
  }
}

TL.instance = function(url,data,callback) {
  TL.loading("start");
	$.ajax({
	  method: "POST",
	  url: url,
	  data: data
	}).done(function( data ) {
      TL.loading("stop");
	    callback(data);
	});
};

