var f = function(element) {
  this.element = element;
  this.on = function(options){
    TL.action(this.element,function(load) {
      if (typeof options === "object") {
        if (typeof options.click_r === "function") {
          $(load).click(function(ev) {
            options.click_r(load);
          });  
        }
        if (typeof options.run === "function") {   
          options.run(load);
        }
        if (!TL.session) {
          if (typeof options.action === "function") {
            options.action(load);
          };
        }else{
          if (typeof options.session === "function") {
            options.session(load);
          };
          if (typeof options.click_s === "function") {
            $(load).click(function(ev) {
              options.click_s(load);
            });  
          }
        }
      }
    }); 
  }
}

